var createLink = function(labelOptions, callback) { var view = Titanium.UI.createView({ width:'auto', height:'auto', layout:'vertical' });

var label = Titanium.UI.createLabel(labelOptions);

label.addEventListener('click', callback);

var lineView = Titanium.UI.createView({ width:label.width, left:label.left, height:1, backgroundColor:label.color ? label.color : 'black', bottom:0 });

view.add(label); view.add(lineView); return view; }

